(() => {
    const labels = document.querySelectorAll('.label-input');
    labels.forEach(label => {
        label.addEventListener('click', () => {
            label.nextElementSibling.focus();
            label.classList.add('efeitooLabel');
        });
    });

    const inputs = document.querySelectorAll('.input-label');
    inputs.forEach(input => {
        input.addEventListener('click', () => {
            input.focus();
            input.previousElementSibling.classList.add('efeitooLabel');
        });
    });

    labels.forEach(label => {
        label.nextElementSibling.addEventListener('focusout', () => {
            if (label.nextElementSibling.value === '') {
                label.classList.remove('efeitooLabel');
            }
        });
    });

    labels.forEach(label => {
        label.nextElementSibling.addEventListener('focusin', () => {
            label.classList.add('efeitooLabel');
        });
    });
    
})();